import axios from 'axios';
import { GET_NOTICE_LIST } from './actionTypes';

//异步获得通知列表的界面
export const getList = (attr, page) => {
	return (dispatch) => {
		//传递了两个参数，pageNum和attr两个参数
		axios.get(`/${attr}/list`, { params: { 'pageNum': page, 'attr': attr } })
			.then((res) => {
				const result = res.data.data;
				const action = {
					type: GET_NOTICE_LIST,
					list: result,
				}
				dispatch(action);
			})
	}
}
