import React, { Component } from 'react'
import Header from '../header/Header'
import Footer from '../footer/Footer'
import { Row, Col, Card, Table } from 'antd'
import { actionCreators } from './store';
import { connect } from 'react-redux';
import './list.css'

const { Column } = Table;
class List extends Component {
    render() {
        return (
            <div>
                <Header />
                <div className='list-container'>
                    <Row>
                        <Col span={14} offset={5}>
                            <Card>
                                <Table sticky size="middle" bordered={false} dataSource={this.props.list} >
                                    <Column title="标题" dataIndex='title' />
                                    <Column title="作者" dataIndex="author" />
                                    <Column title="发布时间" dataIndex="date_time" />
                                </Table>
                            </Card>
                        </Col>
                    </Row>
                </div>
                <Footer />
            </div>
        )
    }
    componentDidMount() {
        console.log(this.props.match.params.attr)

        this.props.getList(this.props.match.params.attr)

    }

}
//返回过来的状态
const mapStateToProps = (state) => ({
    list: state.get('list')
})
const mapDispatchToProps = (dispatch) => ({
    getList(attr) {
        dispatch(actionCreators.getList(attr))
    }
})


export default connect(mapStateToProps, mapDispatchToProps)(List);
