import React, { Component } from 'react'
import Header from '../header/Header'
import Footer from '../footer/Footer'
import { Row, Col, Card, Table, Typography } from 'antd'
import { actionCreators } from './store';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom'
import './list.css'
import { text } from 'body-parser';

const { Column } = Table;
class List extends Component {
    render() {
        return (
            <div>
                <Header />
                <div className='list-container'>
                    <Row>
                        <Col span={14} offset={5}>
                            <Card>
                                <Table sticky size="middle" bordered={false} dataSource={this.props.list}
                                    renderItem={item => <List.Item id={item.get('_id')}>
                                        <Typography.Text ellipsis="true">
                                            <Link target="_black" to={`/detail/${item.get('_id')}`}>{item.get('title')}</Link>
                                        </Typography.Text></List.Item>}>
                                    <Column title="标题" dataIndex='title' render={title => { title }} />
                                    <Column title="作者" dataIndex="author" />
                                    <Column title="发布时间" dataIndex="date_time" />
                                </Table>
                            </Card>
                        </Col>
                    </Row>
                    {console.log(this.props.list)}
                </div>
                <Footer />
            </div>
        )
    }
    componentDidMount() {
        this.props.getList(this.props.match.params.attr)

    }

}
//返回过来的状态
const mapStateToProps = (state) => ({
    list: state.get('list')
})
const mapDispatchToProps = (dispatch) => ({
    getList(attr) {
        dispatch(actionCreators.getList(attr))
    }
})


export default connect(mapStateToProps, mapDispatchToProps)(List);
