import React, { Component } from 'react'
import Header from '../header/Header'
import Footer from '../footer/Footer'
import { Row, Col, Table } from 'antd'
import { actionCreators } from './store';
import { connect } from 'react-redux';
// import { Link } from 'react-router-dom'
import './list.css'
const { Column } = Table;

class List extends Component {
    render() {
        const data = this.props.list.list
        const { total, PAGE_SIZE, PAGE_NUM } = this.props.list
        // console.log(data, data.list.list)
        return (
            <div>
                <Header />
                <div className='list-container'>
                    <Row>
                        <Col span={14} offset={5}>

                            <Table
                                // size={"middle"}
                                bordered={false}
                                rowKey='_id'
                                //分页的配置对象
                                pagination={{
                                    total,
                                    defaultPageSize: PAGE_SIZE,
                                    showQuickJumper: true,
                                    defaultCurrent: 1,
                                    // onChange: this.props.getList(PAGE_NUM),//要传递一个参数选择是哪一页
                                }}
                                dataSource={data.list} >
                                <Column title='标题' dataIndex="title" key='title' />
                                <Column title='author' dataIndex="author" key='author' />
                                <Column title='时间' dataIndex='time' key='time' />
                            </Table>

                        </Col>
                    </Row>

                </div>
                <Footer />
            </div>
        )
    }
    componentDidMount() {
        this.props.getList(this.props.match.params.attr, this.props.match.params.PageNum)
        console.log(this.props)
    }

}
//返回过来的状态
const mapStateToProps = (state) => ({
    list: state.get('list')
})
const mapDispatchToProps = (dispatch) => ({
    getList(attr, PageNum) {
        dispatch(actionCreators.getList(attr, PageNum))
    }
})


export default connect(mapStateToProps, mapDispatchToProps)(List);
