import React, { Component } from 'react'
import { Card, Carousel, List, Typography } from 'antd'
import { connect } from 'react-redux';
import { Link } from 'react-router-dom'
const path = require('path');
const contentStyle = {
    height: '255px',
    color: '#fff',
    lineHeight: '255px',
    textAlign: 'center',
    background: '#364d79',
};
const data = [
    'Racing car sprays burning .',
    'Japanese princess to 255555.',
    'Australian walks 100km ',
];

class Movie extends Component {
    render() {
        return (
            <div>
                <Card hoverable title="强军影视" className="movie-card">
                    <Carousel effect="fade" autoplay>
                        {this.props.list.map((item) => {
                            const url = path.join('/public/video', item.get('url').get(0))
                            console.log(url)
                            return (
                                <div key={item.get('_id')}>
                                    <Link to={`/detail/${item.get('_id')}`}><img style={{ height: '233px', width: '100%' }} src={url} alt='video' /></Link>
                                </div>
                            )

                        })}
                    </Carousel>
                    <List
                        size="small"
                        bordered
                        dataSource={data}
                        renderItem={item => <List.Item>
                            <Typography.Text ellipsis="true">
                                <Link to='/movie_detail'>
                                    {item}
                                </Link>
                            </Typography.Text></List.Item>}>
                    </List>
                </Card>
            </div>
        )
    }
}
const mapStateToProps = (state) => ({
    list: state.get('home').get('videolist')
})
export default connect(mapStateToProps, null)(Movie);
