import React, { Component } from 'react'
import Header from '../header/Header'
import Footer from '../footer/Footer'
import { Row, Col, Card, Table } from 'antd'
import { actionCreators } from './store';
import { connect } from 'react-redux';
// import { Link } from 'react-router-dom'
import './list.css'

const columns = [
    { title: 'Name', dataIndex: 'title', key: 'name', },
    { title: 'Name', dataIndex: 'author', key: 'name', },]

class List extends Component {
    render() {
        const data = this.props.list
        console.log(data)
        return (
            <div>
                <Header />
                <div className='list-container'>
                    <Row>
                        <Col span={14} offset={5}>
                            <Card>
                                {/* <List
                                    size="small"
                                    bordered
                                    dataSource={this.props.list}
                                    renderItem={item => <List.Item id={item.get('_id')}>
                                        <Typography.Text ellipsis="true">
                                            <Link target="_black" to={`/detail/${item.get('_id')}`}>{item.get('title')}</Link>
                                        </Typography.Text></List.Item>}>
                                </List> */}
                                <Table columns={columns} dataSource={this.props.list}>

                                </Table>
                            </Card>
                        </Col>
                    </Row>

                </div>
                <Footer />
            </div>
        )
    }
    componentDidMount() {
        this.props.getList(this.props.match.params.attr)
    }

}
//返回过来的状态
const mapStateToProps = (state) => ({
    list: state.get('list').toJS()
})
const mapDispatchToProps = (dispatch) => ({
    getList(attr) {
        dispatch(actionCreators.getList(attr))
    }
})


export default connect(mapStateToProps, mapDispatchToProps)(List);
