import { fromJS } from 'immutable';
import { GET_NOTICE_LIST } from './actionTypes';
const defaultDetailState = fromJS({
	list: [],
})

export default (state = defaultDetailState, action) => {
	switch (action.type) {
		case GET_NOTICE_LIST:
			return state.merge({
				list: (action.list),
			})

		default:
	}

	return state;

}
